/// <reference types="vite/client" />
import { createClient, SupabaseClient } from '@supabase/supabase-js';

const supabaseUrl = (import.meta.env.VITE_SUPABASE_URL as string) || '';
const supabaseKey = (import.meta.env.VITE_SUPABASE_ANON_KEY as string) || '';

let supabase: SupabaseClient | null = null;

if (supabaseUrl && supabaseKey) {
  supabase = createClient(supabaseUrl, supabaseKey);
}

export { supabase };

export interface PortfolioRecord {
  id?: string;
  slug: string;
  title: string;
  subtitle: string;
  about: string;
  email: string;
  phone: string;
  location: string;
  profileImage: string;
  heroText?: string;
  skills?: Array<{ id: string; name: string; level: number; color: 'cyan' | 'purple' | 'green' }>;
  stats?: Array<{ id: string; value: string; label: string }>;
  services: Array<{ id: string; name: string; description: string; icon: string }>;
  projects: Array<{ id: string; name: string; description: string; image: string; link: string; tags: string[] }>;
  modules?: Array<{ id: string; name: string; description: string; link: string; language: string }>;
  repos?: Array<{ id: string; name: string; description: string; link: string; stars: string; language: string }>;
  docs?: Array<{ id: string; title: string; content: string; category: string }>;
  socialMedia: Array<{ platform: string; url: string }>;
  faqItems?: Array<{ id: string; question: string; answer: string }>;
  templateId: number;
  createdAt?: string;
  updatedAt?: string;
}

// Database uses SNAKE_CASE - convert from our camelCase
const toDatabaseFormat = (data: PortfolioRecord) => ({
  slug: (data.slug || '').toLowerCase().trim(),
  title: (data.title || 'Untitled').trim(),
  subtitle: (data.subtitle || '').trim(),
  about: (data.about || '').trim(),
  email: (data.email || '').trim(),
  phone: (data.phone || '').trim(),
  location: (data.location || '').trim(),
  profileimage: data.profileImage || '',  // PostgreSQL lowercases: profileImage ‚Üí profileimage
  herotext: data.heroText || '',
  skills: data.skills || [],              // DevFolio specific: skill bars
  stats: data.stats || [],                // DevFolio specific: stat metrics
  services: data.services || [],
  projects: data.projects || [],
  modules: data.modules || [],            // DevFolio specific: code modules
  repos: data.repos || [],                // DevFolio specific: repositories
  docs: data.docs || [],                  // DevFolio specific: documentation
  socialmedia: data.socialMedia || [],   // PostgreSQL lowercases: socialMedia ‚Üí socialmedia
  faqitems: data.faqItems || [],
  templateid: data.templateId || 1,      // PostgreSQL lowercases: templateId ‚Üí templateid
  updatedat: new Date().toISOString()    // PostgreSQL lowercases: updatedAt ‚Üí updatedat
});

// Convert from database SNAKE_CASE to our camelCase
const fromDatabaseFormat = (dbData: any): PortfolioRecord => ({
  id: dbData.id,
  slug: dbData.slug,
  title: dbData.title,
  subtitle: dbData.subtitle,
  about: dbData.about,
  email: dbData.email,
  phone: dbData.phone,
  location: dbData.location,
  profileImage: dbData.profileimage || '',
  heroText: dbData.herotext || '',
  skills: dbData.skills || [],
  stats: dbData.stats || [],
  services: dbData.services || [],
  projects: dbData.projects || [],
  modules: dbData.modules || [],
  repos: dbData.repos || [],
  docs: dbData.docs || [],
  socialMedia: dbData.socialmedia || [],
  faqItems: dbData.faqitems || [],
  templateId: dbData.templateid || 1,
  createdAt: dbData.createdat,
  updatedAt: dbData.updatedat
});

// Save portfolio to Supabase
export const savePortfolio = async (data: PortfolioRecord) => {
  try {
    if (!supabase) {
      throw new Error('Supabase is not configured.');
    }

    const dbData = toDatabaseFormat(data);
    console.log('Saving to DB:', dbData);

    const { data: result, error } = await supabase
      .from('portfolios')
      .upsert([dbData]);

    if (error) {
      console.error('DB Error Details:', JSON.stringify(error));
      throw new Error(`Database error: ${error.message}`);
    }

    console.log('Portfolio saved!', result);
    return result;
  } catch (error) {
    console.error('Error saving portfolio:', error);
    throw error;
  }
};

// Get portfolio by slug
export const getPortfolioBySlug = async (slug: string) => {
  try {
    if (!supabase) {
      throw new Error('Supabase is not configured.');
    }

    const normalizedSlug = (slug || '').toLowerCase().trim();

    const { data, error } = await supabase
      .from('portfolios')
      .select('*')
      .eq('slug', normalizedSlug)
      .single();

    if (error) {
      if (error.code === 'PGRST116') {
        console.warn('Portfolio not found:', normalizedSlug);
        return null;
      }
      throw error;
    }

    console.log('Portfolio fetched:', data?.slug);
    return fromDatabaseFormat(data);
  } catch (error) {
    console.error('Error fetching portfolio:', error);
    return null;
  }
};

// Check if slug exists
export const checkSlugExists = async (slug: string) => {
  try {
    if (!supabase) {
      throw new Error('Supabase is not configured.');
    }

    const normalizedSlug = (slug || '').toLowerCase().trim();

    const { data, error } = await supabase
      .from('portfolios')
      .select('id')
      .eq('slug', normalizedSlug)
      .single();

    if (error && error.code === 'PGRST116') {
      return false; // Slug is available
    }

    if (error) throw error;
    return !!data; // Slug exists
  } catch (error) {
    console.error('Error checking slug:', error);
    return true; // Assume taken on error (safe default)
  }
};

// Get all portfolios
export const getAllPortfolios = async (limit: number = 50) => {
  try {
    if (!supabase) {
      throw new Error('Supabase is not configured.');
    }

    const { data, error } = await supabase
      .from('portfolios')
      .select('*')
      .order('createdat', { ascending: false })
      .limit(limit);

    if (error) throw error;
    return (data || []).map(fromDatabaseFormat);
  } catch (error) {
    console.error('Error fetching portfolios:', error);
    return [];
  }
};

