            >
              <Eye className="w-5 h-5" />
              View My Portfolio
            </button>
            <button
              onClick={onBack}
              className="flex items-center justify-center gap-2 bg-slate-700 hover:bg-slate-600 text-white font-semibold py-3 rounded-xl transition-all"
            >
              Create Another
            </button>
          </motion.div>

          {/* Extra Info */}
          <motion.div
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            transition={{ delay: 0.7 }}
            className="mt-8 pt-8 border-t border-slate-700"
          >
            <p className="text-sm text-slate-400 text-center">
              üí° Save this link and share it with anyone! Your portfolio is now publicly accessible.
            </p>
          </motion.div>
        </div>
      </motion.div>
    </motion.div>
  );
};

